// src/modules/counter.js

import { act } from "react-dom/test-utils";

// const initialState = {
//   number: 0,
// };

// const counter = (state = initialState, action) => {
//   switch (action.type) {
//     default:
//       return state;
//   }
// };

// export default counter;
let initialState = {
  count: 0,
};

function reducer(state = initialState, action) {
  console.log(action);
  if (action.type === "INCREMENT") {
    // INCREMENT는 dispatch에서 호출?한 값
    return { ...state, count: state.count + 1 }; // 기존 state 값은 유지하고(...state) count는 +1을 하겠다
  } //리턴 값이 reducer가 되는것

  // switch문으로 쓰면 이렇게
  //   switch(action.type){
  //     case "INCREMENT":
  //         return{...state, count: state.count + 1}
  //     default:
  //         return{...state}
  //   }
}

export default reducer;
