import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import { addTodo } from "../redux/modules/counter";
import shortid from "shortid";

const Form = () => {
  const [title, setTitle] = useState({
    id: 0,
    title: "",
    body: "",
    isDone: false,
  });
  const [body, setBody] = useState({
    id: 0,
    title: "",
    body: "",
    isDone: false,
  });
  const [todo, setTodo] = useState({
    id: 0,
    title: "",
    body: "",
    isDone: false,
  });

  const dispatch = useDispatch();

  const id = shortid.generate();

  const changeTitle = (e) => {
    return setTitle(e.target.value);
  };
  const changeBody = (e) => {
    return setBody(e.target.value);
  };

  const submit = (e) => {
    e.preventDefault();
    if (title.title === "" || body.body === "") return;
    setTodo({
      id,
      title: title.title,
      body: body.body,
      isDone: false,
    });

    dispatch(addTodo({ ...todo, id }));
  };
  return (
    <div>
      <form onSubmit={submit}>
        제목
        <input type="text" value={title.title} onChange={changeTitle}></input>
        내용<input type="text" value={body.body} onChange={changeBody}></input>
        <button>확인</button>
      </form>
    </div>
  );
};

export default Form;
