import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import { addTodo } from "./redux/modules/counter";
import shortid from "shortid";

const Form = () => {
  const [title, setTitle] = useState({
    id: 0,
    title: "",
    body: "",
    isDone: false,
  });
  const [body, setBody] = useState({
    id: 0,
    title: "",
    body: "",
    isDone: false,
  });
  const dispatch = useDispatch();

  const changeTitle = (e) => {
    console.log(e.target.value);
    return setTitle(e.target.value);
  };
  const changeBody = (e) => {
    console.log(e.target.value);
    return setBody(e.target.value);
  };

  const submit = (e) => {
    e.preventDefault();
    if (title && body === "") return;

    dispatch(
      addTodo({
        id: shortid.generate(),
        title: title,
        body: body,
        isDone: false,
      })
    );
    setBody("");
    setTitle("");
  };
  return (
    <div>
      <form onSubmit={submit}>
        <input type="text" value={title.title} onChange={changeTitle}></input>
        <input type="text" value={body.body} onChange={changeBody}></input>
        <button>확인</button>
      </form>
    </div>
  );
};

export default Form;
